package Sample_0321;

public class For_statment1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 1; i <= 10; i++) {
			System.out.println(i);
		}
		int j = 10;
		while (j >= 1) {
			System.out.println(j * 10); // j*10
			j--;

		}
	}

}
